const response = $input.first().json;

// Try to parse the AI response as JSON
let parsedResponse;
try {
  // The AI response might be in the output field or directly in the response
  const aiOutput = response.output || response.text || response;

  // If it's a string, try to extract JSON from it
  if (typeof aiOutput === 'string') {
    // Try to find JSON in the response
    const jsonMatch = aiOutput.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      parsedResponse = JSON.parse(jsonMatch[0]);
    } else {
      // If no JSON found, create a default response
      parsedResponse = {
        answer: aiOutput,
        category: 'Other',
        answerFound: true
      };
    }
  } else {
    parsedResponse = aiOutput;
  }
} catch (error) {
  // If parsing fails, return the raw output
  parsedResponse = {
    answer: response.output || response.text || "I'm sorry, I couldn't process your request.",
    category: 'Other',
    answerFound: false
  };
}

// Ensure all required fields exist
return {
  answer: parsedResponse.answer || "I'm sorry, I couldn't process your request.",
  category: parsedResponse.category || 'Other',
  answerFound: parsedResponse.answerFound !== false
};
